sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var requestedUser = request.pathParams.user_name;
	if(!requestedUser || requestedUser.contains("ni_query")){
		response.setError(new sn_ws_err.NotFoundError('User name not provided'));
		response.setStatus(404);
		return;
	}


	var requestedUserGr = new GlideRecord('sys_user');
	var userExist = requestedUserGr.get('user_name', requestedUser);

	if(!userExist){
		response.setError(new sn_ws_err.NotFoundError('Provided user does not exist'));
		response.setStatus(404);
		return;
	}

	var request_ni_query = request.queryParams.ni_query;


	var gr = new GlideAggregate('x_58872_needit_needit');
	var queryStr = request_ni_query + "^u_requested_for=" + requestedUserGr.sys_id;
	gr.addEncodedQuery(queryStr);
	gr.addAggregate('COUNT');
	gr.groupBy('u_request_type');
	gr.query();

	var needItRecs= 0;
	var niUserRecs = [];
	var user = {};

	while(gr.next()){
		var categoryRecsCount = gr.getAggregate('COUNT');
		var recType = gr.u_request_type.getDisplayValue();
		needItRecs += parseInt(categoryRecsCount);
		niUserRecs.push({'category': recType, 'count': categoryRecsCount});

	}
	var body = {};
	body.userNeedItRecs = needItRecs;
	body.catCounts = niUserRecs;
	body.user = {'name': requestedUserGr.user_name, 'sys_id': requestedUserGr.sys_id};

	gs.info("GALIA2::: api is working body is: " + JSON.stringify(body));
	response.setBody(body);

})(request, response);
