sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    var answer = [];

    var number = request.pathParams.number.toString();

    //Declare variables for the possible names in the requested body
    var requestBody = request.body.data;
    var short_description = requestBody.short_description;
    var state = requestBody.state;
    var priority = requestBody.priority;
    var requested_for = requestBody.requested_for;
    var request_type = requestBody.request_type;
    var when_needed = requestBody.when_needed;
    var requested_for_email = requestBody.requested_for_email;

    //Generate 404 if number not provided
    if (!number) {
        response.setError(new sn_ws_err.NotFoundError("No Number defined"));
        response.setStatus(404);
        return;
    }

    //Generate 404 if request body contains no or invalid data
    if (!short_description && !state && !priority && !requested_for && request_type && !when_needed && !requested_for_email) {
        response.setError(new sn_ws_err.NotFoundError("Object contains no or invalid properties"));
        response.setStatus(404);
        return;
    }

    var needItGr = new GlideRecord("x_58872_needit_needit");
    needItGr.addQuery('number', number);
    needItGr.query();

    if (!needItGr.hasNext()) {
        response.setError(new sn_ws_err.NotFoundError("No NeedIt record founded"));
        response.setStatus(404);
        return;
    }

    if (needItGr.next()) {
        if (short_description) {
            needItGr.short_description = short_description;
        }
        if (priority) {
            needItGr.priority = priority;
        }
        if (requested_for) {
            needItGr.u_requested_for = requested_for;
        }
        if (request_type) {
            needItGr.u_request_type = request_type;
        }
        if (when_needed) {
            needItGr.u_when_needed = when_needed;
        }
        if (requested_for_email) {
            needItGr.u_requested_for_email = requested_for_email;
        }
        if (state) {
            needItGr.state = state;
        }

        needItGr.update();

    }

    //Set a 200 status for success
    response.setStatus(200);
    //Create a json obj with details of the updated record
    var updatedNeedItObj = {
		"short_description": needItGr.short_description,
        "sys_id": needItGr.sys_id,
		"requested_for": needItGr.u_requested_for.getDisplayValue(),
		"when_needed": needItGr.u_when_needed,
		"requested_type": needItGr.u_request_type,
		"state": needItGr.state,
		"requested_for_email": needItGr.u_requested_for_email
    };

	response.setBody(updatedNeedItObj);



})(request, response);
