sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var userNiRecs = [];

	var user_name = request.pathParams.user_name;
	if(!user_name){
		response.setError(sn_ws_err.NotFoundError('User name not provided'));
		response.setStatus(404);
		return;
	}

	var requestedUserGr = new GlideRecord('sys_user');
	var userExist = requestedUserGr.get('user_name', user_name);

	if(!userExist){
		response.setError(new sn_ws_err.NotFoundError('Provided user does not exist'));
		response.setStatus(404);
		return;
	}

	var niGr = new GlideRecord("x_58872_needit_needit");
	niGr.addEncodedQuery('u_requested_for=' + requestedUserGr.sys_id);
	niGr.query();

	while(niGr.next()){
		gs.info("GALIS2222");
		var niObj = {
			"Number": niGr.number,
			"Requested for": niGr.u_requested_for,
			"Short description:": niGr.short_description,
			"Assign to": niGr.assigned_to,
			"Status": niGr.state,
			"Priority": niGr.priority
		};

		userNiRecs.push(niObj);
	}

	gs.info("result is: " + userNiRecs);

	response.setBody(userNiRecs);

})(request, response);
